import Alert from '@mui/material/Alert'
import PlayArrowIcon from '@mui/icons-material/PlayArrow'

function ManageDocker({app}) {

  return (
    <>
      {
        app.State === 'running' ? (
          <Alert elevation={10} severity="success">
            {app.Status}
          </Alert>
        ) : (
        app.State === 'paused' ? (
            <PlayArrowIcon sx={{ height: 20, width: 20 }} />
        ) : (
          <Alert elevation={10} severity="error">
            Container error
          </Alert>
        ))
      }
    </>
  )
}

export default ManageDocker
