import Alert from '@mui/material/Alert'
import IconButton from '@mui/material/IconButton'
import Box from '@mui/material/Box'
import PlayArrowIcon from '@mui/icons-material/PlayArrow'
import PauseIcon from '@mui/icons-material/Pause'
import RestartIcon from '@mui/icons-material/RestartAlt'
import StopIcon from '@mui/icons-material/Stop'

function ManageDocker({app}) {

  return (
    <Box sx={{ display: 'flex', alignItems: 'center', pl: 2, pt: 2 }}>
      <IconButton aria-label="play/pause">
        {app.State === 'running' ? <PauseIcon sx={{ height: 30, width: 30 }} /> : app.State === 'paused' ? <PlayArrowIcon sx={{ height: 30, width: 30 }} /> : <PlayArrowIcon sx={{ height: 30, width: 30 }} color="disabled" />}
      </IconButton>
      <IconButton aria-label="play/pause">
        {app.State ? <RestartIcon sx={{ height: 30, width: 30 }} /> : <RestartIcon sx={{ height: 30, width: 30 }} color="disabled" />}
      </IconButton>
      <IconButton aria-label="play/pause">
        {app.State ? <StopIcon sx={{ height: 30, width: 30 }} /> : <StopIcon sx={{ height: 30, width: 30 }} color="disabled" />}
      </IconButton>
    </Box>

  )
}

export default ManageDocker


