    //       <PauseIcon sx={{ height: 30, width: 30 }} />
    //     </IconButton>
    // }
  }

  return (
    <Box sx={{ display: 'flex', alignItems: 'center', pl: 2, pt: 2 }}>
{/*      {app.State === 'running' 
