import { useState, useEffect } from 'react'
import Grid from '@mui/material/Grid'
import Typography from '@mui/material/Typography'
import AppsIconOk from '@mui/icons-material/Apps'
import AppsIconError from '@mui/icons-material/AppsOutage'
import Card from '@mui/material/Card'
import CardMedia from '@mui/material/CardMedia'
import CardContent from '@mui/material/CardContent'
import CardActionArea from '@mui/material/CardActionArea'
import Link from '@mui/material/Link'
import container from './../features/container'

function Apps({containerName, image, title, url}) {

  const [cont, setCont] = useState([])
  const [app, setApp] = useState([])

  useEffect(() => {
    retrieveContainers()
  }, [])

  function retrieveContainers(){
    container.getContainer(containerName).then(response => { setCont(response.data.container) })
    //console.log(cont)
    renderCont(cont)
  }

  function renderCont(contData){
    contData.map((data) => {
      setApp(data)
      //console.log(data)
    })
  }

  return (
      <Grid item>
        <Card sx={{ width: 200, height: 300 }}>
          <CardActionArea>
            <Link href={url} target="_blank" underline="none" color="inherit">
              <Grid container spacing={1} justifyContent="center" display="flex">
                <Grid item>
                  { app.State === 'running' ? <AppsIconOk sx={{ fontSize: 50 }}/> : <AppsIconError sx={{ fontSize: 50 }}/> }
                </Grid>
                <Grid item>
                  <Typography variant="h4" align="center">
                    {title}
                  </Typography>
                </Grid>
              </Grid>
              <CardMedia
                component="img"
                height="100"
                image={image}
                alt={title}
              />
            </Link>
          </CardActionArea>
          <CardContent>
              asdf
          </CardContent>
        </Card>
      </Grid>
  )
}

export default Apps
